mapper.insert:
INSERT INTO alert_processing (
    alert_id, user_id, handled_at, handler_name, item_type, rack_id, comment
) VALUES (
    #{alertId}, #{userId}, #{handledAt}, #{handlerName}, #{itemType}, #{rackId}, #{comment}
)

mapper.select:
SELECT id, alert_id, user_id, handled_at, handler_name, item_type, rack_id, comment
FROM alert_processing 
WHERE alert_id = #{alertId}
